rdf:Description.elmo:query:

prefix elmo: <http://bp4mc2.org/elmo/def#>
prefix html: <http://www.w3.org/1999/xhtml/vocab#>
construct {
	<urn:header> elmo:html "<img src='/images/ldt-logo.png'/>"
}
where {}


prefix elmo: <http://bp4mc2.org/elmo/def#>
construct {
	?presentation ?p ?o.
}
where {
	graph <@STAGE@> {
		?presentation rdf:type elmo:Presentation.
		?presentation ?p ?o.
		FILTER (?p = rdfs:label ||
				?p = rdf:type ||
				?p = rdfs:comment ||
				?p = elmo:uses
		)
	}
}


prefix elmo: <http://bp4mc2.org/elmo/def#>
select ?rep ?urlpattern ?object
where {
	graph <@STAGE@> {
		{
			?rep rdf:type elmo:Representation.
			OPTIONAL {?rep elmo:url-pattern ?urlpattern}
			OPTIONAL {
				?rep elmo:applies-to ?pattern.
				?pattern rdf:type ?object.
			}
		}
		UNION
		{
			?rep rdf:type ?type.
			BIND(str(?rep) as ?urlpattern)
			FILTER (?type = elmo:Container || ?type = elmo:VersionContainer || ?type = elmo:Production)
		}
	}
}
